requireCssFile('/resource/KcloudData__jQuery2/ui/css/ui-lightness/jquery-ui-1.9.1.custom.min.css');

							function requireCssFile(filename) {
								var fileref = document.createElement('link');
								fileref.setAttribute('rel', 'stylesheet');
								fileref.setAttribute('type', 'text/css');
								fileref.setAttribute('href', filename);
								document.getElementsByTagName('head')[0].appendChild(fileref);
							}

							var j$ = jQuery.noConflict();
							var iframe_url = '{!URLFOR('/apex/KcloudData__VerifyAccountData')}';
							var child_domain = iframe_url.substring(0, iframe_url.indexOf('/', 9));
							var parent_domain = window.location.protocol + '//' + window.location.host;


								var j$modalDialog = j$('<div></div>')
									.html('<iframe id="iframeContentId" src="' + iframe_url + '?id={!Account.Id}&core.apexpages.devmode.url=0&parent_domain=' + parent_domain + '" frameborder="0" height="100%" width="100%" marginheight="0" marginwidth="0" scrolling="yes" />')
									.dialog({
										autoOpen: false,
										title: '{!Account.Name}',
										resizable: false,
										width: 800,
										height: 550,
										minHeight: 190,
										autoResize: true,
										modal: true,
										draggable: false
									});
								j$(".ui-dialog").find(".ui-widget-header").css({
									"background": "#0F314B",
									"border": "1px solid #0F314B"
								});

								j$modalDialog.dialog('open');


							j$.receiveMessage(
								function(e) {
									var cross_result = j$.deparam(e.data);
									if (cross_result.action == 'close_modal_dialog_confirm') {
										j$("#div2").html("");
										j$modalDialog.dialog('destroy');
										document.location.reload(true);
									} else {
										j$("#div2").html("");
										j$modalDialog.dialog('destroy');
									}
								},
								child_domain
							);
